package Scaler;

public class Trial {
    public static void main(String[] args) {
        Trial ss = new Trial();
        System.out.println(ss.solve("GGAGAGBAGAAGA"));
        // System.out.println(ss.solve(""));
        System.out.println(ss.solve("GAABAGGAGG"));
    }


    public static int solve(String str) {
        char[] arr = str.toCharArray();
        int n = arr.length;
        long ans = Long.valueOf(0);
        int ps[] = new int[n];
        if(arr[0] == 'G'){
            ps[0] = 1;
        }else{
            ps[0] = 0;
        }
        for (int i = 1; i < n; i++) {
            if (arr[i] == 'G')
                ps[i] = ps[i-1] + 1;
            else
                ps[i] = ps[i-1];
        }
        for (int i = 0; i < n; i++) {
            if (arr[i] == 'A') {
                //ans += (ps[n - 1] - ps[i]) % 1000000007;
                ans += (ps[n - 1] - ps[i]);
            }
        }
        return (int)(ans% 1000000007);
    }


}
